mapper.insert:
insert into Board (
BoardSubject, BoardContent,
BoardWriter, BoardDate, UserID, BoardHit
) values(
#{BoardSubject}, #{Content},
#{BoardWriter}, #{BoardDate}, #{UserID}, #{BoardHit}
)

insert into BoardFile (
BOriginalFileName, BStoredFileName, BFileSize,BoardID
) values(
#{OriginalFileName}, #{StoredFileName}, #{FileSize},#{Bno}
)
